package com.bibinet.intelibuild.mvp.presenter;import android.app.Activity;import com.bibinet.intelibuild.mvp.model.CollectProductInfoActivityModel;import com.bibinet.intelibuild.mvp.view.CollectProudctInfoActivityView;import java.io        .IOException;import okhttp3.Call;import okhttp3.Callback;import okhttp3.Response;/** * Created by bibinet on 2018-1-13. */public class CollectProductInfoPresenter extends BasePresenter {    private CollectProductInfoActivityModel collectProductInfoActivityModel;    private CollectProudctInfoActivityView collectProudctInfoActivityView;    public CollectProductInfoPresenter(CollectProudctInfoActivityView collectProudctInfoActivityView) {        this.collectProudctInfoActivityView = collectProudctInfoActivityView;        this.collectProductInfoActivityModel=new CollectProductInfoActivityModel();    }    public void postCollecProductInfo(final Activity activity,String userId, String productFileNumb, String productFileName, String productName, String doStandard){        collectProductInfoActivityModel.postCollectProductInfo(userId,productFileNumb, productFileName, productName, doStandard, new Callback() {            @Override            public void onFailure(Call call, final IOException e) {                activity.runOnUiThread(new Runnable() {                    @Override                    public void run() {                        collectProudctInfoActivityView.onPostCollectProductFailed(e.getMessage());                    }                });            }            @Override            public void onResponse(Call call, Response response) throws IOException {                collectProudctInfoActivityView.onPostCollectProductSucess();            }        });    }}