package com.bibinet.intelibuild.mvp.presenter;import android.app.Activity;import com.bibinet.intelibuild.mvp.model.ResetPasswordActivityModel;import com.bibinet.intelibuild.mvp.view.ResetPasswordActivityView;import java.io        .IOException;import okhttp3.Call;import okhttp3.Callback;import okhttp3.Response;/** * Created by bibinet on 2018-1-15. */public class ResetPasswordActivityPresenter extends BasePresenter {    private ResetPasswordActivityView resetPasswordActivityView;    private ResetPasswordActivityModel resetPasswordActivityModel;    public ResetPasswordActivityPresenter(ResetPasswordActivityView resetPasswordActivityView) {        this.resetPasswordActivityView = resetPasswordActivityView;        this.resetPasswordActivityModel=new ResetPasswordActivityModel();    }    public void resetPassword(final Activity activity,String userId, String password, String newPasswrod, String enSurePassword){        resetPasswordActivityModel.resetPassword(userId,password, newPasswrod, enSurePassword, new Callback() {            @Override            public void onFailure(Call call, final IOException e) {                activity.runOnUiThread(new Runnable() {                    @Override                    public void run() {                        resetPasswordActivityView.onResetPasswordFailed(e.getMessage());                    }                });            }            @Override            public void onResponse(Call call, Response response) throws IOException {                activity.runOnUiThread(new Runnable() {                    @Override                    public void run() {                        resetPasswordActivityView.onResetPasswordSucess();                    }                });            }        });    }}