package com.bibinet.intelibuild.project.bean;import java.io.Serializable;/** * Created by bibinet on 2018-1-12. */public class ProductInfoBean implements Serializable {    private String productName;    private int icon;    public ProductInfoBean(String productName) {        this.productName = productName;        this.icon = icon;    }    public String getProductName() {        return productName;    }    public void setProductName(String productName) {        this.productName = productName;    }    public int getIcon() {        return icon;    }    public void setIcon(int icon) {        this.icon = icon;    }}