package com.bibinet.intelibuild.project.ui.activity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import com.bibinet.intelibuild.R;import com.bibinet.intelibuild.mvp.presenter.ResetPasswordActivityPresenter;import com.bibinet.intelibuild.mvp.view.ResetPasswordActivityView;import com.bibinet.intelibuild.project.ui.baseui.BaseActivity;import butterknife.BindView;import butterknife.ButterKnife;import butterknife.OnClick;/** * Created by bibinet on 2017-12-27. */public class RestPasswrodActivity extends BaseActivity implements ResetPasswordActivityView{    @BindView(R.id.inputPassword)    EditText inputPassword;    @BindView(R.id.newPassword)    EditText newPassword;    @BindView(R.id.EnsurePassword)    EditText EnsurePassword;    @BindView(R.id.btn_ResetPassword)    Button btnResetPassword;    private ResetPasswordActivityPresenter resetPasswordActivityPresenter;    @Override    protected int getChildlayout() {        return R.layout.activity_resetpassword;    }    @Override    protected void initView() {        title.setText("修改密码");        titleBackImage.setVisibility(View.VISIBLE);        resetPasswordActivityPresenter=new ResetPasswordActivityPresenter(this);    }    @OnClick({R.id.title_imageleft,R.id.btn_ResetPassword})    public void onViewClicked(View view) {        switch (view.getId()) {            case R.id.title_imageleft:                finish();                break;            case R.id.btn_ResetPassword:                resetPasswordActivityPresenter.resetPassword(this,"",inputPassword.getText().toString(),newPassword.getText().toString()                        ,EnsurePassword.getText().toString());                break;            default:                break;        }    }    @Override    public void onResetPasswordSucess() {    }    @Override    public void onResetPasswordFailed(String msg) {    }}