package com.bibinet.intelibuild.project.ui.activity;import android.content.Intent;import android.view.View;import android.view.animation.AlphaAnimation;import android.view.animation.Animation;import android.widget.LinearLayout;import com.bibinet.intelibuild.R;import com.bibinet.intelibuild.project.ui.baseui.BaseActivity;import com.bibinet.intelibuild.project.ui.baseui.MainActivity;import butterknife.BindView;/** * Created by bibinet on 2018-1-24. */public class SplashActivity extends BaseActivity {    @BindView(R.id.animationLayout)    LinearLayout animationLayout;    @Override    protected int getChildlayout() {        return R.layout.activity_splash;    }    @Override    protected void initView() {        titleLayout.setVisibility(View.GONE);        AlphaAnimation alphaAnimation=new AlphaAnimation(0.7f,1.0f);        alphaAnimation.setDuration(2000);        alphaAnimation.setAnimationListener(new Animation.AnimationListener() {            @Override            public void onAnimationStart(Animation animation) {            }            @Override            public void onAnimationEnd(Animation animation) {                startActivity(new Intent(SplashActivity.this, MainActivity.class));                finish();            }            @Override            public void onAnimationRepeat(Animation animation) {            }        });        animationLayout.startAnimation(alphaAnimation);    }}